import random
suits = ('Hearts', 'Diamonds', 'Spades', 'Clubs')
ranks = ('Two', 'Three', 'Four', 'Five', 'Six', 'Seven', 'Eight', 'Nine', 'Ten', 'Jack', 'Queen', 'King', 'Ace')
values = {'Two': 2, 'Three': 3, 'Four': 4, 'Five': 5, 'Six': 6, 'Seven': 7, 'Eight': 8, 'Nine': 9, 'Ten': 10, 'Jack': 10, 'Queen': 10, 'King': 10, 'Ace': 11}

def get_value(cards):
    value = 0
    for card in cards:
        value += values[card[0]]
    return value
while True:
    dealer_hand = []
    player_hand = []
    for _ in range(2):
        dealer_hand.append(random.choice(list(zip(ranks, suits))))
        player_hand.append(random.choice(list(zip(ranks, suits))))
    print("Player's Hand: ", player_hand)
    print('Value of Hand: ', get_value(player_hand))
    print('Dealer Hand: ', dealer_hand[0])
    hit_or_stand = input('Would you like to [H]it or [S]tand? ').lower()
    if hit_or_stand == 'h':
        player_hand.append(random.choice(list(zip(ranks, suits))))
        print("Player's Hand: ", player_hand)
        print('Value of Hand: ', get_value(player_hand))
        if get_value(player_hand) > 21:
            print('Bust! Player loses.')
            break
    elif hit_or_stand == 's':
        while get_value(dealer_hand) < 17:
            dealer_hand.append(random.choice(list(zip(ranks, suits))))
            print('Dealer Hand: ', dealer_hand)
            print('Value of Hand: ', get_value(dealer_hand))
        if get_value(dealer_hand) > 21 and get_value(dealer_hand) < get_value(player_hand):
            print('Player wins!')
            break
        elif get_value(dealer_hand) > get_value(player_hand):
            print('Dealer wins!')
            break
        else:
            print("It's a tie!")
            break
