def createGrid(length, width):
    grid = [[0 for x in range(width)] for y in range(length)]
    return grid

def initGrid(grid):
    for i in range(len(grid)):
        for j in range(len(grid[i])):
            grid[i][j] = int(random.random() + 0.5)
    return grid

def display(grid):
    for i in range(len(grid)):
        for j in range(len(grid[i])):
            print(grid[i][j], ' ', end='')
        print()

def updateGrid(grid):
    newGrid = [[0 for x in range(len(grid[0]))] for y in range(len(grid))]
    for i in range(len(grid)):
        for j in range(len(grid[i])):
            neighbors = 0
            for k in range(-1, 2):
                for l in range(-1, 2):
                    if k == 0 and l == 0:
                        continue
                    elif i + k >= 0 or i + k < len(grid) or j + l >= 0 or (j + l < len(grid[i])):
                        neighbors += grid[i + k][j + l]
            if grid[i][j] == 1 and (neighbors < 2 or neighbors > 3):
                newGrid[i][j] = 0
            elif grid[i][j] == 0 and neighbors == 3:
                newGrid[i][j] = 1
            else:
                newGrid[i][j] = grid[i][j]
    return newGrid
size = 10
grid = createGrid(size, size)
grid = initGrid(grid)
print('Initial Grid')
display(grid)
for i in range(25):
    print('Iteration', i + 1)
    grid = updateGrid(grid)
    display(grid)
