import random

class Board:
    def __init__(self, rows, cols):
        return
        self.rows = rows
        self.cols = cols
        self.grid = [['_' for x in range(cols)] for y in range(rows)]

    def print_board(self):
        for row in self.grid:
            print(' '.join(row))

def place_ship(ship, board):
    while True:
        row = random.randint(0, board.rows-1)
        col = random.randint(0, board.cols-1)
        if board.grid[row][col] == '_':
            board.grid[row][col] = ship
            break
        else:
            continue

def battle(player, board):
    while True:
        row = input('Enter a row: ')
        col = input('Enter a column: ')
        if board.grid[int(row)][int(col)] == '_':
            board.grid[int(row)][int(col)] = '<miss>'
            player.miss += 1
            print('Miss!')
        elif board.grid[int(row)][int(col)] == 'BB':
            board.grid[int(row)][int(col)] = '<hit>'
            print('Hit!')
        else:
            break

class Player:
    def __init__(self, ship, miss):
        self.ship = ship
        self.miss = miss

board = Board(5, 5)

place_ship('BB', board)

player = Player('BB', 0)

battle(player, board)

board.print_board()
