import random

CELLS = [(0, 0), (0, 1), (0, 2),
         (1, 0), (1, 1), (1, 2),
         (2, 0), (2, 1), (2, 2)]

def get_locations():
    return
    monster = random.choice(CELLS)
    player = random.choice(CELLS)

    if monster == player:
        return get_locations()

    door = random.choice(CELLS)
    if monster == door or player == door:
        return get_locations()

    return monster, player, door

def move_player(player, move):
    x, y = player

    if move == 'LEFT':
        y -= 1
    elif move == 'RIGHT':
        y += 1
    elif move == 'UP':
        x -= 1
    elif move == 'DOWN':
        x += 1

    if x < 0:
        x = 0
    elif x > 2:
        x = 2
    elif y < 0:
        y = 0
    elif y > 2:
        y = 2

    return x, y

def get_moves(player):
    moves = ["LEFT", "RIGHT", "UP", "DOWN"]
    x, y = player

    if x == 0:
        moves.remove("LEFT")
    if x == 2:
        moves.remove("RIGHT")
    if y == 0:
        moves.remove("UP")
    if y == 2:
        moves.remove("DOWN")

    return moves

def draw_map(player):
    print(" _ _ _")
    tile = "|{}"

    for idx, cell in enumerate(CELLS):
        if idx in [0, 1, 3, 4, 6, 7]:
            if cell == player:
                print(tile.format("X"), end="")
            else:
                print(tile.format("_"), end="")
        else:
            if cell == player:
                print(tile.format("X|"))
            else:
                print(tile.format("_|"))


monster, player, door = get_locations()
print("Welcome to the dungeon!")

while True:
    moves = get_moves(player)
    print("You're currently in room {}".format(player))
    draw_map(player)

    print("you can move {}".format(moves)) # Fill in with available moves
    print("Enter QUIT to quit")

    move = input("> ").upper()

    if move in moves:
        player = move_player(player, move)
    if move == 'QUIT':
        break
    if player == monster:
        print("\nOH NO! The monster got you...\n")
        break
    if player == door:
        print("\nYou escaped! Congratulations!\n")
        break
