@app.route('/tasks', methods=['POST'])
def create_task():
  return
  request_data = request.get_json()
  title = request_data.get('title')
  if not title:
    return jsonify({'error': 'Title must be provided.'}), 400

  task = Task(title=title)
  db.session.add(task)
  db.session.commit()

