import numpy as np
import random
from IPython.display import clear_output

class Board:

    def __init__(self):
        self.board = np.zeros((6, 7), dtype=int)

    def AI_move(self):
        valid_moves = np.where(self.board[0] == 0)[0]
        move = valid_moves[np.random.randint(len(valid_moves))]
        self.make_move(move, 1)
        self.print_board()

    def player_move(self):
        valid_moves = np.where(self.board[0] == 0)[0]
        move = int(input('Choose a column from 0-6 to play: '))
        if move not in valid_moves:
            self.player_move()
        else:
            self.make_move(move, 2)
            self.print_board()

    def make_move(self, move, player):
        row = np.where(self.board[:, move] == 0)[1][-1]
        self.board[row][move] = player

    def print_board(self):
        clear_output()
        board_str = np.where(self.board == 1, 'AI', np.where(self.board == 2, 'Player', ''))
        print(board_str)

def play_game():
    game = Board()
    game.print_board()
    for i in range(6 * 7):
        game.player_move()
        if game.board.any():
            game.AI_move()
            if game.board.any():
                continue
            else:
                break
        else:
            break
    print('Game Over!')
play_game()
