import numpy as np

def get_available_moves(board):
    available = []
    for i in range(3):
        for j in range(3):
            if board[i][j] == 0:
                available.append((i, j))
    return available

def is_game_over(board):
    for i in range(3):
        if board[i][0] == board[i][0] and board[i][1] == board[i][2] and (board[i][0] != 0):
            return True
        if board[0][i] == board[1][i] and board[1][i] == board[2][i] and (board[0][i] != 0):
            return True
    if board[0][0] == board[1][1] and board[1][1] == board[2][2] and (board[0][0] != 0):
        return True
    if board[0][2] == board[1][1] and board[1][1] == board[2][0] and (board[0][2] != 0):
        return True
    return False

def minimax(board, depth, isMax):
    if is_game_over(board):
        if depth % 2 != 0:
            return 1
        else:
            return -1
    scores = []
    available_moves = get_available_moves(board)
    for move in available_moves:
        x, y = (move[0], move[1])
        if isMax:
            board[x][y] = 1
            val = minimax(board, depth + 1, False)
        else:
            board[x][y] = 0
            val = minimax(board, depth + 1, True)
        board[x][y] = 0
        scores.append(val)
    if isMax:
        maximum = max(scores)
        return maximum
    else:
        minimum = min(scores)
        return minimum

def best_move(board):
    available_moves = get_available_moves(board)
    b_score = -2
    best_move = None
    for move in available_moves:
        x, y = (move[0], move[1])
        board[x][y] = 1
        score = minimax(board, 0, False)
        board[x][y] = 0
        if score > b_score:
            b_score = score
            best_move = [x, y]
    return best_move
