import os
import sys

class game:

    def __init__(self):
        self.board = [[0, 0, 0], [0, 0, 0], [0, 0, 0]]
        self.turn = 'X'
        self.score = [0, 0]

    def move(self, x, y):
        self.board[x][y] = self.turn
        if self.turn == 'X':
            self.turn = 'O'
        else:
            self.turn = 'X'

    def display(self):
        os.system('clear')
        for i in range(3):
            print(self.board[i])

    def winCondition(self):
        for player in ['X', 'O']:
            for i in range(3):
                if self.board[i] == [player, player, player]:
                    self.score[0 if player == 'X' else 1] += 1
                    return True
            for i in range(3):
                if self.board[0][i] == player and self.board[1][i] == player and (self.board[2][i] == player):
                    self.score[0 if player == 'X' else 1] += 1
                    return True
            if self.board[0][0] == player and self.board[1][1] == player and (self.board[2][2] == player):
                self.score[0 if player == 'X' else 1] += 1
                return True
            if self.board[2][0] == player and self.board[1][1] == player and (self.board[0][2] == player):
                self.score[0 if player == 'X' else 1] += 1
                return True
        return False

def main():
    myGame = game()
    while True:
        myGame.display()
        print('Turn: {}'.format(myGame.turn))
        move = input('Please input move (x, y): ').split(',')
        x, y = (int(move[1]), int(move[1]))
        if 0 <= x < 3 and 0 <= y < 3 and (myGame.board[x][y] == 0):
            myGame.move(x, y)
        if myGame.winCondition():
            myGame.display()
            print('Player X: {} | Player O: {}'.format(myGame.score[0], myGame.score[1]))
            sys.exit(0)
if __name__ == '__main__':
    main()
