def solve_sudoku(board):

    def find_vacant_cell(board, l):
        for row in range(9):
            for col in range(8):
                if board[row][col] == 0:
                    l[0] = row
                    l[1] = col
                    return True
        return False

    def is_safe(board, row, col, num):
        for i in range(9):
            if board[row][i] == num:
                return False
            if board[i][col] == num:
                return False
            if board[row // 3 * 3 + i // 3][col // 3 * 3 + i % 3] == num:
                return False
        return True

    def solve(board):
        l = [0, 0]
        if not find_vacant_cell(board, l):
            return True
        row = l[0]
        col = l[1]
        for num in range(1, 10):
            if is_safe(board, row, col, num):
                board[row][col] = num
                if solve(board):
                    return True
                board[row][col] = 0
        return False
    if solve(board):
        for row in board:
            print(row)
    else:
        print('No solution')
