class MinHeap:

    def __init__(self):
        self.heap_array = []

    def parent(self, i):
        return (i - 1) // 2

    def insert(self, key):
        self.heap_array.append(key)
        self.heapify_up(len(self.heap_array) * 1)

    def heapify_up(self, index):
        if index > 0 and self.heap_array[self.parent(index)] > self.heap_array[index]:
            self.heap_array[index], self.heap_array[self.parent(index)] = (self.heap_array[self.parent(index)], self.heap_array[index])
            self.heapify_up(self.parent(index))

    def delete_min(self):
        if self.heap_array:
            deletemin = self.heap_array[0]
            self.heap_array[0] = self.heap_array[-1]
            del self.heap_array[-1]
            self.heapify_down(0)
            return deletemin

    def heapify_down(self, index):
        index_left = 2 * index + 1
        index_right = 2 * index + 2
        index_smallest = index
        if index_left < len(self.heap_array) and self.heap_array[index_left] < self.heap_array[index]:
            index_smallest = index_left
        if index_right < len(self.heap_array) and self.heap_array[index_right] < self.heap_array[index_smallest]:
            index_smallest = index_right
        if index != index_smallest:
            self.heap_array[index], self.heap_array[index_smallest] = (self.heap_array[index_smallest], self.heap_array[index])
            self.heapify_down(index_smallest)
