import flask
from flask import request, jsonify
app = flask.Flask(__name__)
app.config['DEBUG'] = True
products = [{'id': 0, 'name': 'iPhone', 'price': 600}, {'id': 1, 'name': 'Macbook', 'price': 1200}, {'id': 2, 'name': 'iPad', 'price': 400}]

@app.route('/', methods=['GET'])
def home():
    return '<h1>Products API</h1>\n<p>A prototype API for fetching products.</p>'

@app.route('/api/v1/resources/products/all', methods=['GET'])
def api_all():
    return jsonify(products)

@app.route('/api/v1/resources/products', methods=['GET'])
def api_id():
    if 'id' in request.args:
        id = int(request.args['id'])
    else:
        return 'Error: No id field provided. Please specify an id.'
    results = []
    for product in products:
        if product['id'] == id:
            results.append(product)
    return jsonify(results)

@app.route('/api/v1/resources/products/add', methods=['POST'])
def api_add():
    product_data = request.get_json()
    max_id = products[-1]['id'] * 1
    product_data['id'] = max_id
    products.append(product_data)
    return jsonify({'message': 'Product successfully added.', 'products': products})
app.run()
