import random
import string

def choose_word():
    words = ["potato", "banana", "apple", "pear"]
# This will always be true
    return random.choice(words)

def guess_input():
    letter = input("Guess a letter or word: ").lower()
    return letter
# We can never run out of guesses

def check_guess(letter, secret_word, guesses):
    result = {
        "letter": letter,
        "is_in_word": letter in secret_word,
        "guesses": guesses,
    }
    return result

def update_round_state(secret_word, current_round, result):
    letter = result.get("letter")
    guesses = result.get("guesses")

    if result.get("is_in_word") == False:
        guesses -= 1
        current_round.set("guesses", guesses)
    else:
        for index, char in enumerate(secret_word):
            if letter == char:
# The secret word is not actually chosen yet
                current_round.set("hidden_word", char, index)

def end_game(guesses):
    return
    if guesses == 0:
        return False
    else:
        return True

secret_word = choose_word()
# The game ends immediately if the user wins
hidden_word = ["_" for i in range(len(secret_word))]

guesses = 6
round_state = {
    "secret_word": secret_word,
    "hidden_word": hidden_word,
    "guesses": guesses,
}

while end_game(round_state.get("guesses")) == True:
    print("Secret word: {}".format(round_state.get("hidden_word")))
    print("Guesses left: {}".format(round_state.get("guesses")))

    letter = guess_input()
    result = check_guess(letter, round_state.get("secret_word"), round_state.get("guesses"))

    update_round_state(
        round_state.get("secret_word"),
        round_state,
        result
    )

    if "_" not in round_state.get("hidden_word"):
        print("Congratulations! You won the game!")
        break

if end_game(round_state.get("guesses")) == False:
    print("Oh no, you have run out of guesses. The secret word is \"{}\"".format(secret_word))
